import {
  Card,
  CardContent,
  CardActions,
  Typography,
  FormControl,
  InputLabel,
  OutlinedInput,
  MenuItem,
  TextField,
  Button,
  Select,
} from "@mui/material";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4 } from "uuid";
import axios from "axios";

function Login({ setAdmitted, baseURL }) {
  const navigate = useNavigate();
  const [account, setAccount] = useState({});

  const handleLogIn = async () => {
    console.log("in handlelogin");
    if (account.userName === "" || account.password === "") {
      console.log("inside if condition");
      return alert("all required fields must not be empty!");
    }
    try {
      const response = await axios.post(
        `${baseURL}/credentialsCheck`,
        {
          userName: account.userName,
          password: account.password,
        },
        {
          headers: {
            "content-type": "application/x-www-form-urlencoded",
          },
        }
      );
      console.log(response);
      if (response) {
        setAdmitted(true);
        navigate("/home");
      } else {
        alert("User name or password are not correct");
      }
    } catch (error) {
      console.log(error);
    }
  };

  const changeAccountDetails = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setAccount({ ...account, [name]: value });
    console.log(account);
  };

  return (
    <div className="container">
      <Card component="form" className="form" data-testid="addNewPost-card">
        <CardContent className="formFields">
          <FormControl sx={{ minWidth: "100%" }}>
            <InputLabel
              required
              htmlFor="userName-field"
              data-testid="addNewPost-postTitleLabel"
            >
              User Name
            </InputLabel>
            <OutlinedInput
              error={false}
              id="login-userName"
              label="UserName"
              fullWidth
              value={account.userName}
              onChange={(event) => {
                changeAccountDetails(event);
              }}
              data-testid="addNewPost-postTitle"
            />
          </FormControl>
          <FormControl sx={{ minWidth: "100%" }}>
            <InputLabel
              required
              htmlFor="userName-field"
              data-testid="addNewPost-postTitleLabel"
            >
              Password
            </InputLabel>
            <OutlinedInput
              error={false}
              id="login-userName"
              label="UserName"
              fullWidth
              value={account.password}
              onChange={(event) => {
                changeAccountDetails(event);
              }}
              data-testid="addNewPost-postTitle"
            />
          </FormControl>
        </CardContent>
        <CardActions>
          <Button
            variant="contained"
            size="large"
            data-testid="addNewPost-submitBtn"
            onClick={handleLogIn}
          >
            Log In
          </Button>
          <Button
            variant="contained"
            size="large"
            data-testid="addNewPost-submitBtn"
            onClick={handleLogIn}
          >
            Sign Up
          </Button>
        </CardActions>
      </Card>
    </div>
  );
}

export default Login;
