import React from "react";

function UserCard({ userId, userName, userImage }) {
  return (
    <div>
      <ListItem
        alignItems="flex-start"
        key={`userCard-${userId}`}
        className="userCard"
        data-testid={`userCard-${userId}`}
      >
        <Card className="userCard">
          <ListItemButton disableGutters>
            <CardContent>
              <Typography
                variant="h5"
                gutterBottom
                data-testid={`userName-${userId}`}
              >
                {userName}
              </Typography>
              <Typography
                variant="body1"
                gutterBottom
                data-testid={`postContent-${postId}`}
              >
                {readMore ? postContent : postContent.substring(0, 200)}
              </Typography>
            </CardContent>
          </ListItemButton>
          <CardActions>
            {isAddTagBtn && (
              <AddTagButton
                dataTestId={`postAddTagBtn-${postId}`}
                onClick={(e) => handleAddTagClick(e, postId)}
              />
            )}
            {isTag &&
              tagsNameArr.map((tagName, index) => (
                <Tag
                  key={`post-${index}-${tagName}`}
                  tagName={tagName}
                  postId={postId}
                  handleOnClick={handleTagClick}
                  selectedTagId={selectedTagId}
                  isDisabled={isTagDisabled}
                />
              ))}
            <IconButton
              aria-label="readMore"
              size="small"
              data-testid={`postContent-readMoreButton`}
              onClick={() => handleReadMore()}
            >
              <AutoStoriesIcon></AutoStoriesIcon>
            </IconButton>
            <IconButton
              aria-label="dislike"
              size="small"
              data-testid={`postDislikeBtn-${postId}`}
              onClick={handleDislikeClick}
            >
              {didUserDislikePost ? (
                <ThumbDownAltIcon
                  color="error"
                  data-testid={`fullDislikeIcon-${postId}`}
                />
              ) : (
                <ThumbDownOffAltIcon
                  color="error"
                  data-testid={`hollowDislikeIcon-${postId}`}
                />
              )}
            </IconButton>
            <Typography
              variant="string"
              data-testid={`postDislikeNum-${postId}`}
              color="red"
            >
              {postDislikes !== undefined ? postDislikes.length : 0}
            </Typography>
            <IconButton
              aria-label="like"
              size="small"
              data-testid={`postLikeBtn-${postId}`}
              onClick={handleLikeClick}
            >
              {didUserLikePost ? (
                <ThumbUpAltIcon
                  color="success"
                  data-testid={`fullLikeIcon-${postId}`}
                />
              ) : (
                <ThumbUpOffAltIcon
                  color="success"
                  data-testid={`hollowLikeIcon-${postId}`}
                />
              )}
            </IconButton>
            <Typography
              variant="string"
              data-testid={`postLikeNum-${postId}`}
              color="green"
            >
              {postLikes !== undefined ? postLikes.length : 0}
            </Typography>
          </CardActions>
        </Card>
      </ListItem>
    </div>
  );
}

export default UserCard;
