import React from "react";
import {
  CardContent,
  ListItemButton,
  Card,
  ListItem,
  Typography,
} from "@mui/material";
import Image from "mui-image";

function UserCard({ userId, userName, userImage }) {
  return (
    <div>
      <ListItem
        alignItems="flex-start"
        key={`userCard-${userId}`}
        className="userCard"
        data-testid={`userCard-${userId}`}
      >
        <Card className="userCard">
          <ListItemButton disableGutters>
            <CardContent className="userCard">
              <Typography
                variant="h6"
                letterSpacing={1.5}
                gutterBottom
                data-testid={`userName-${userId}`}
              >
                {userName}
              </Typography>
              <Image
                src={userImage}
                alt={`${userName}'s profile Image`}
                style={{
                  width: 100,
                  height: 100,
                  borderRadius: "10px",
                }}
              />
            </CardContent>
          </ListItemButton>
        </Card>
      </ListItem>
    </div>
  );
}

export default UserCard;
